import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux'
import { useDropzone } from 'react-dropzone';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import * as S from './Cadastro.style'

const Cadastro = () => {
    const [marca, setMarca] = useState();
    const [ano, setAno] = useState();
    const [modelo, setModelo] = useState();
    const [placa, setPlaca] = useState();
    const [files, setFiles] = useState([]);
    const [data, setData] = useState([])

    const { getInputProps } = useDropzone({
        accept: {
            'image/*': []
        },
        onDrop: acceptedFiles => {
            setFiles(acceptedFiles?.map(file => Object.assign(file, {
                preview: URL.createObjectURL(file)
            })));
        }
    });



    const addItem = () => {
        const val = ({
            marca: marca,
            ano: ano,
            modelo: modelo,
            placa: placa,
            file: files[0]?.preview
        })
        var dataLocal = []
        dataLocal = JSON.parse(localStorage.getItem("items_dados"));
        if (dataLocal == null) {
            dataLocal = []
        }
        dataLocal.push(val);

        console.log('o que vem em data', dataLocal)
        localStorage.setItem("items_dados", JSON.stringify(dataLocal))

    }





    return (
        <S.Content>
            <h3 className='titleForm'>Cadastro de novos ve√≠culos</h3>
            <Form>
                <Form.Group>
                    <Form.Label>Imagem:</Form.Label>
                    <input id="addImage" {...getInputProps()} />
                    <div className='previewImg'>
                        {files[0]?.preview && <p id='remove' onClick={() => setFiles([])}>x</p>}
                        <img className='preview' src={files[0]?.preview} />

                    </div>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Marca:</Form.Label>
                    <Form.Control type="text" placeholder="informe a marca"
                        onChange={(e) => setMarca(e.target.value)} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Modelo:</Form.Label>
                    <Form.Control type="text" placeholder="informe o modelo"
                        onChange={(e) => setModelo(e.target.value)} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Ano:</Form.Label>
                    <Form.Control type="text" placeholder="informe o ano"
                        onChange={(e) => setAno(e.target.value)} />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Placa:</Form.Label>
                    <Form.Control type="text" placeholder="informe a placa"
                        onChange={(e) => setPlaca(e.target.value)} />
                </Form.Group>
                <Button onClick={addItem}>Adicionar</Button>
            </Form>
        </S.Content>

    )
}

export default Cadastro;
